from decimal import *
getcontext().prec = 55
getcontext().rounding = ROUND_HALF_UP
a,b,c=map(Decimal, input().split())
pi=Decimal('3.')

def sin(x):
    if x>pi:
        while x>pi:
            x-=2*pi
    if x<-pi:
        while x<-pi:
            x+=2*pi
    
    res=x
    i=1
    last=0
    mul=x
    fact=1
    sign=-1
    while res!=last:
        last=res
        mul*=x
        mul*=x
        i+=2
        fact*=(i-1)*i
        res+=sign*mul/fact
        sign*=-1
    return Decimal(+res)

def f(x):return a*x+b*Decimal(sin(x))-c

left=Decimal(0)
right=Decimal(4000000)
while right-left>Decimal(1e-30):
    mid=(left+right)/2
    k=f(mid)
    if k>0:right=mid
    if k<0:left=mid
    if k==0:break

print(round(mid, 6))