nn=640
for i in range(nn):
    a,b=L[nn-i-1]
    L.append((a,-b))

n,k=map(int,input().split())
def solve(n,k):
    if n==k and n%2:return False
    sol=[]
    odd=False
    if n%2:
        odd=True
        n+=1
    for i in range(n//2):sol.append(L[i])
    for j in range(n//2):
        a,b=sol[j]
        sol.append((-a,-b))
    x,y=-2**31, 0
    res=[]
    for i in range(n):
        res.append((x,y))
        a,b=sol[i]
        x+=a
        y+=b
    if odd:res.pop(-1)
    return res

